        if (nums[0] > 1) {
            int bound = min(k, nums[0]-1);
            added += bound;
            res += (long long) (1 + bound)*bound/2;
        }
        for(int i=0;i<nums.size()-1;i++) {
            if (nums[i+1] > nums[i]+1 && added < k) {
                int bound = min(k - added, nums[i+1]-nums[i]-1);
                added += bound;
                res += (long long) (nums[i] +1 +nums[i] + bound)*bound/2;

            }
            if(added >= k){
                return res;
            }
        }
        if (added < k) {
            int bound = k - added;
            res += (long long)(nums.back()+1+nums.back()+bound)*bound/2;
        }
        return res;
    }
};
