class Solution {
    public long minimalKSum(int[] nums, int k) {
        Arrays.sort(nums);

        // fill gaps
        long result = 0;
        long c = 1;
        for (int i : nums) {
            if (i > c) {
                while (c < i && k > 0) {
                    result += c;
                    c++;
