        sort(nums.begin(), nums.end());
        int n = nums.size();
        long long sum = 0;
        for(int i = 0; i < n; i++) {
            while(i < n - 1 && nums[i] == nums[i + 1]) i++;
            if(nums[i] <= k) {
                k++;
                sum += nums[i];
            }
        }
        return k * 1L * (k + 1) / 2 - sum;
    }
};


