public:
    vector<vector<int>> findDifference(vector<int>& nums1, vector<int>& nums2) {
    unordered_set<int>set1(nums1.begin(), nums1.end()), set2(nums2.begin(),nums2.end());
    vector<int> diff1;//create a vector to store all element which is not present in nums2 but present in nums1
    vector<int> diff2;////create a vector to store all element which is not present in nums1 but present in nums2

    for(auto num : set1){
        if(set2.find(num) == set2.end()){ // check for every set1 element, if they are not present in set2 then we push_back that element in diff1 container.
            diff1.push_back(num);
        }
    }
    for (auto num : set2) {// check for every set2 element, if they are not present in set1 then we push_back that element in diff2 container.
        if (set1.find(num) == set1.end()) {
            diff2.push_back(num);
        }
    }

    return { diff1, diff2 }; // return list 
   }

};
