        Arrays.sort(nums2);

        int i1 = 0;
        int i2 = 0;

        ArrayList<Integer> li1 = new ArrayList<>();
        ArrayList<Integer> li2 = new ArrayList<>();

        while(i1 < nums1.length || i2 < nums2.length) {
            if (i1 < nums1.length - 1 && nums1[i1] == nums1[i1 + 1]) {
                i1++;
            }
            else if (i2 < nums2.length - 1 && nums2[i2] == nums2[i2 + 1]) {
                i2++;
