            if(mp2.find(var)==mp2.end() && mp1[var]!=0)
            {
                tmp_nums1.push_back(nums1[i]);
                mp1[var] = 0;                       // after taking the value make that map value 0 for removing the duplication
            }
        }
        diff.push_back(tmp_nums1);

        // fidning the elements of array2 that are not avaliable in array1
        // storing that type of element in some tmp vector
        vector<int> tmp_nums2;
        for(int i=0;i<nums2.size();i++)
        {
            int var = nums2[i];
            // searching for the element in first array map
            // if it is not present then push it to our tmp vector
            if(mp1.find(var)==mp1.end() && mp2[var]!=0)
            {
                tmp_nums2.push_back(nums2[i]);
                mp2[var] = 0;                      // after taking the value make that map value 0 for removing the duplication
            }
        }
        diff.push_back(tmp_nums2);

        return diff;
    }
};
