```
vector<vector<int>> findDifference(vector<int> &nums1, vector<int> &nums2)
{
    int m = nums1.size();
    int n = nums2.size();

    // Initialize a 2D vector with two empty vectors to store the results
    vector<vector<int>> ans(2);

    // Sort both input vectors in non-decreasing order
    sort(nums1.begin(), nums1.end());
    sort(nums2.begin(), nums2.end());

    // Initialize two pointers to traverse both vectors
    int ptr1 = 0;
    int ptr2 = 0;

    // Find elements in nums1 that are not present in nums2
    while (ptr1 < m && ptr2 < n)
    {
        if (nums1[ptr1] < nums2[ptr2])
        {
            // If the current element in nums1 is smaller than the current element in nums2,
            // add it to the first result vector as it will not be present in the nums2 any further
            ans[0].push_back(nums1[ptr1]);

            // Advance the nums1 pointer to the next unique element
            int ele = nums1[ptr1];
            while (ptr1 < m && nums1[ptr1] == ele)
                ptr1++;
        }
        else if (nums1[ptr1] == nums2[ptr2])
        {
            // If the current elements in both vectors are equal, skip them
            // and advance both pointers to the next unique elements in their respective vectors
            int ele = nums1[ptr1];
            while (ptr1 < m && nums1[ptr1] == ele)
                ptr1++;

            ele = nums2[ptr2];
            while (ptr2 < n && nums2[ptr2] == ele)
                ptr2++;
        }
        else
            ptr2++;
    }
    // Add any remaining elements in nums1 to the first result vector
    while (ptr1 < m)
    {
        int ele = nums1[ptr1];
        ans[0].push_back(ele);
        while (ptr1 < m && nums1[ptr1] == ele)
            ptr1++;
    }

    // Reset both pointers to traverse the vectors again
    ptr1 = 0, ptr2 = 0;

    // Find elements in nums2 that are not present in nums1
    while (ptr1 < m && ptr2 < n)
    {
        if (nums1[ptr1] > nums2[ptr2])
        {
            // If the current element in nums1 is larger than the current element in nums2,
            // add it to the second result vector as it will not be present in the nums2 any further
            ans[1].push_back(nums2[ptr2]);

            // Advance the nums2 pointer to the next unique element
            int ele = nums2[ptr2];
            while (ptr2 < n && nums2[ptr2] == ele)
                ptr2++;
        }
        else if (nums1[ptr1] == nums2[ptr2])
        {
            // If the current elements in both vectors are equal, skip them
            // and advance both pointers to the next unique elements in their respective vectors
            int ele = nums1[ptr1];
            while (ptr1 < m && nums1[ptr1] == ele)
                ptr1++;

            ele = nums2[ptr2];
            while (ptr2 < n && nums2[ptr2] == ele)
                ptr2++;
        }
        else
            ptr1++;
    }
    // Add any remaining elements in nums2 to the second result vector
    while (ptr2 < n)
    {
        int ele = nums2[ptr2];
        ans[1].push_back(ele);
        while (ptr2 < n && nums2[ptr2] == ele)
            ptr2++;
    }

    // Return both result vectors
    return ans;
}
};
