class Solution {
    public List<List<Integer>> findDifference(int[] nums1, int[] nums2) {

        List<List<Integer>> res = new ArrayList<>();

        res.add(getDistValues(nums1, nums2));
        res.add(getDistValues(nums2, nums1));

        return res;

    }

    private List<Integer> getDistValues(int[] nums1, int[] nums2){
        List<Integer> list = new ArrayList<>();

        boolean[] isPresent = new boolean[2001];    // we take the size as 2000 so that we can keep track of -ve(negative) and +ve(positive) values;
                                                    // if we keep the size as 1000 it will throw error (ArrayIndexOutOfBounds) for -ve values;

        for(int i:nums2)
            isPresent[i+1000]=true;           // for every value in the array to set the [i+1000] as "True";
			// We are adding 1000 to i to accomodate the -ve values;

        for(int i:nums1){
            if(!isPresent[i+1000]){             // if the value at the index [i+1000] is false that means the number doesn't exist
                                                // we will add that number to the list;
                list.add(i);
                isPresent[i+1000]=true;         // we set the [i+1000] to true so that we don't store the duplicate values;
            }

        }

        return list;

    }
}

