class Solution {
public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n = piles.size();
        // total n piles and k coins we can choose.
        // so for any dp[i][j] it will store the max coins we
        // get using i piles and j coins used.
        vector<vector<int>> dp(n + 1, vector<int>(k + 1, 0));
        // Since we want to be dealing with the sums of coins,
        // it would be real convenient, if our piles just stored
        // the prefix sum instead. This can be done on the piles
        // matrix directly, however, it is a good practice to not
        // to change anything provided in the problem. Make pref
        vector<vector<int>> pref(n, vector<int>()); // prefix-sum
        for (int i = 0; i < n; i++) {
            pref[i].resize(piles[i].size()); // uneven matrix so resize()
            pref[i][0] = piles[i][0]; // initialize for pref_sum
            for (int j = 1; j < piles[i].size(); j++) {
                // calculating prefix sum
                pref[i][j] = pref[i][j - 1] + piles[i][j];
            }
        }

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= k; j++) {
                // Let the coins chosen from the current pile be cur
                for (int cur = 0; cur <= piles[i - 1].size(); cur++) {
                    // Check for all combinations of coins that sum up to j coins.
                    // We can pick w coins from this pile and j-w coins from previous piles (dp[i-1][j-w]).
                    // i.e., (j-w) + w = j coins in total.
                    if(cur <= j)
                        if(cur > 0){
                            // if cur > 0 we can consider prefix sum since in-bounds
                            dp[i][j] = max(dp[i][j], dp[i - 1][j - cur] + pref[i - 1][cur - 1]);
                        }
                        else{
                            dp[i][j] = max(dp[i][j], dp[i - 1][j - cur]);
                        }
                }
            }
        }
        return dp[n][k];
    }
};


