class Solution {
public:
    //memoization Solution
    int helper(int i, vector<vector<int>>& piles, int k, vector<vector<int>>& dp){
        //base condition
        if(i == piles.size() || k <= 0){
            return 0;
        }

        //check if this condition is reached before
        if(dp[i][k] != -1){
            return dp[i][k];
        }

        long long ans = INT_MIN;
        for(int j = 0; j < piles[i].size(); j++){
            //if j becomes greater than k-1 break
            if(j > k-1)
                break;

            //take starting j+1 coins from the ith pile
            long long take = piles[i][j] + helper(i+1, piles, k-j-1, dp);

            //store the max value in ans
            ans = max(ans, take);
        }

        //donot take any coins from ith piles
        long long ntake = helper(i+1, piles, k, dp);
        ans = max(ans, ntake);

        //memoize it
        return dp[i][k] = ans;
    }
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        //dp vector
        vector<vector<int>> dp(piles.size(), vector<int>(k+1,-1));

        //prefix vector
        vector<vector<int>> prefix;
        for(auto a: piles){
            int n = a.size();
            vector<int> temp(n);
            temp[0] = a[0];
            for(int i = 1; i < n; i++){
                temp[i] = temp[i-1] + a[i];
            }
            prefix.push_back(temp);
        }
        return helper(0, prefix, k, dp);
    }
};
