import numpy as np
class Solution:
    def maxValueOfCoins(self, piles: List[List[int]], k: int) -> int:
        best = np.zeros(k+1)
        for pile in piles:
            temp = best.copy()
            for used, value in enumerate(accumulate(pile), 1):
                if used > k: break
                temp2 = best+value
                temp2 = np.concatenate((np.zeros(used), temp2[:-used]))
                temp = np.maximum(temp, temp2)
            best = temp
        return int(best[-1])
