class Solution {
public:
    int helper(vector<vector<int>>& piles, int k, int ind, vector<vector<int>> & dp){
        if(ind<0)return 0;
        if(k==0)return 0;
        if(dp[ind][k]!=-1)return dp[ind][k];
        int a=helper(piles, k, ind-1, dp);

        int ans=a;
        for(int i=0; i<piles[ind].size(); i++){
            int b=0;
            if(i+1<=k){
                b=piles[ind][i]+helper(piles, k-i-1, ind-1, dp);
                ans=max(ans, b);
            }
        }
        return dp[ind][k]=ans;
    }
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n=piles.size();
        for(int i=0; i<piles.size(); i++){
            for(int j=1; j<piles[i].size(); j++){
                piles[i][j]+=piles[i][j-1];
            }
        }
        vector<vector<int>> dp(n, vector<int>(k+1, -1));
        return helper(piles, k, n-1, dp);
    }
};
