class Solution {
public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {

        vector<vector<int>> dp(piles.size(), vector<int> (k + 1, -1));
        return helper(piles, 0, k, dp);
    }

    int helper(vector<vector<int>> &piles, int i, int k, vector<vector<int>> &dp)
    {
        if(i >= piles.size() || k == 0)
            return 0;

        if(dp[i][k] != -1)
            return dp[i][k];

        int mx = helper(piles, i + 1, k, dp), sum = 0;
        for(int j = 0; j < piles[i].size() && j < k; j++)
            mx = max(mx, (sum += piles[i][j]) + helper(piles, i + 1, k - j - 1, dp));

        return dp[i][k] = mx;
    }
};
