class Solution {
    fun maxValueOfCoins(A: List<List<Int>>, K: Int): Int {
        var M = A.size
        var dp = Array(M + 1) { IntArray(K + 1 ) { 0 } }
        for (i in M - 1 downTo 0) {
            for (k in 1..K) {
                var (t, best) = Pair(0, 0)
                for (j in 0 until Math.min(A[i].size, k)) {
                    t += A[i][j]
                    var cand = t + dp[i + 1][k - 1 - j]
                    best = Math.max(best, cand)
                }
                dp[i][k] = Math.max(best, dp[i + 1][k]) // ✅ include or 🚫 exclude i-th row's best candidate
            }
        }
        return dp[0][K]
    }
}
```

