class Solution {
public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int n=piles.size();

        // dp[i][j] = maximum money after taking j coins from first i piles.
        vector<vector<int> > dp(n+1, vector<int>(k+1, -2000000000));

        dp[0][0]=0;

        for(int i=0; i<n; i++){
            for(int j=0; j<=k; j++){

                // do not take any coin from this pile.
                dp[i+1][j]=max(dp[i+1][j], dp[i][j]);

                int sum=0, cnt=0;
                for(int p: piles[i]){
                    sum+=p;
                    cnt++;
                    if(j+cnt<=k)
                        // take first cnt coins from this pile.
                        dp[i+1][j+cnt]=max(dp[i+1][j+cnt], dp[i][j]+sum);
                }
            }
        }
        return dp[n][k];
    }
};
```

