class Solution {
public:
    int f(int i, int k, vector<vector<int>>& prefixPiles, vector<vector<int>>& dp){
        if(i<0 || k==0) return 0;

        //if value is available for a particular index, use it
        if(dp[i][k]!=-1) return dp[i][k];
        int temp=prefixPiles[i].size();
        int n=min(temp, k);
