class Solution {
public:
int dp[1001][2001];
int solve(vector<vector<int>> &piles, int idx, int k)
{
    if (k == 0)
        return 0;
    if (idx >= piles.size())
        return 0;
    if (dp[idx][k] != -1)
        return dp[idx][k];

    int ans = INT_MIN;
    int s = 0;
    int res = solve(piles, idx + 1, k);// if not choosing idxth pile
    for (int j = 0; j < piles[idx].size() && j<=k-1; j++)
    {
       s = s + piles[idx][j];
       ans = max({ans, s + solve(piles, idx + 1, k - (j + 1)), res});
    }
    if (ans < 0) return dp[idx][k] = 0;
    return dp[idx][k] = ans;
}
int maxValueOfCoins(vector<vector<int>> &piles, int k)
{
    memset(dp, -1, sizeof(dp));
    return solve(piles, 0, k);
}
};
