                        current_sum += piles[i - 1][current_coins - 1];
                    }
                    dp[i][coins] = Math.Max(dp[i][coins], dp[i - 1][coins - current_coins] + current_sum);
                }
            }
        }
        return dp[n][k]; 
    }
}
