    int m = nums[n - 1].size() - 1;
    res = max(res, func(nums, k, n - 1));
    while (i <= m and k > 0)
    {
        sum += nums[n - 1][i];
        res = max(res, sum + func(nums, k - 1, n - 1));
        i++;
        k--;
    }

