class Solution {
private:
    int dp[1001][2001];
    int fun(vector<vector<int>>& piles, int i, int k){
        if(k==0 || i==piles.size()) return 0;
        if(dp[i][k]!=-1) return dp[i][k];
        int skip = fun(piles, i+1, k);
        int take = 0, sum = 0;
        for(int cur = 0, count = k; cur < piles[i].size() && count; cur++){
            sum += piles[i][cur]; count--;
            take = max(take, sum + fun(piles, i+1, count));
        }
        return dp[i][k] = max(skip, take);
    }
public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        memset(dp, -1, sizeof dp);
        return fun(piles, 0, k);
    }
};

