class Solution {
    public int maxValueOfCoins(List<List<Integer>> piles, int k) {
        int n= piles.size();
        int[][] memo= new int[n][k+1];
        for(int i=0; i<n; i++) Arrays.fill(memo[i], -1);
        return maxValue(0, k, new ArrayList<>(piles), memo);
    }

    int maxValue(int i, int k, ArrayList<List<Integer>> piles, int[][] memo){
        if(k==0) return 0;
        int n= piles.size();
        if(i==n) return 0;

        if(memo[i][k]!=-1) return memo[i][k];

        List<Integer> pile= piles.get(i);
        int len= pile.size(), canTake= Math.min(len, k);
        int thisMaxValue= maxValue(i+1, k, piles, memo);
        int taken= 0, takenValue= 0;
        for(int coin:pile){
            if(taken==canTake) break;
            taken++;
            takenValue+= coin;
            thisMaxValue= Math.max(thisMaxValue, takenValue + maxValue(i+1, k-taken, piles, memo));
        }
        return memo[i][k]= thisMaxValue;
    }
}

