class Solution {
public:
    int maxValueOfCoins(vector<vector<int>>& piles, int k) {
        int m = piles.size();
        // prefix sum
        vector<vector<int>> pfs(m);
        int all_sum = 0;
        int all_cnt = 0;
        for(int i=0;i<m;i++){
            auto& pile = piles[i];
            auto& sums = pfs[i];
            int pile_nums = 0;
            for(int num: pile){
                int sum = sums.empty()? num: (num+sums.back());
                sums.push_back(sum);
                all_sum += num;
                all_cnt++;
                pile_nums++;
                if(pile_nums==k){
                    break;
                }
            }
        }

        if(all_cnt<=k){
            return all_sum;
        }

        vector<vector<int>> dp(m,vector<int>(k+1,0));

        auto get =[&](int i,int num){
            auto& sums = pfs[i];
            int len = sums.size();
            if(len==0||num==0){
                return 0;
            }
            return sums[min(len-1,num-1)];
        };

        for(int i=0; i<m;i++){
            for(int j=1;j<=k;j++){
                for(int get_here =0;get_here<=j&&get_here<=pfs[i].size();get_here++){
                    int get_before = j-get_here;
                    dp[i][j] = max(dp[i][j], get(i,get_here)+(i==0?0:dp[i-1][get_before]));
                }

            }
        }

        return dp.back().back();
    }


};
