	# --- recursive helper --- :
	def recursive(brackets, path="", res=[], left=0, right=0):
            # print(brackets, path, brackets//2, left, right)
            if not brackets:
                res.append(path)
            else:
                if right < left:
                    recursive(brackets-1, path+")", res, left, right+1)
                if left < n: # n should not be decremented as we decrement the number of brackets 
                    recursive(brackets-1, path+"(", res, left+1, right)

            return res

	# --- main logic ---
	return recursive(2*n, "", [], 0, 0)
