class Solution {
public:
    vector<string> generateParenthesis(int n) {
        //pair of pair, <current_buffer,<open count,closed count>>
        queue<pair<string,pair<int,int>>>q;
        q.push({"",{0,0}});
        vector<string>ans;
        while(!q.empty())
        {
            pair<string,pair<int,int>>p = q.front();
            q.pop();
            //if count of both open and closed parenthesis count is n, we found one permutation, add it to ans;
            if(p.second.first==n&&p.second.second==n){
                ans.push_back(p.first);
