class Solution {
    public List<String> generateParenthesis(int n) {
        List<String> output = new ArrayList<>();
        permutationBalancedParenthesis(n, n, "", output);
        return output;
    }

    public void permutationBalancedParenthesis(int open, int close, String balancedParentheses, List<String> output) {
        /* leaf node in recursive tree */
        if(open == 0 && close == 0){
            output.add(balancedParentheses);
            return;
        } 

        /* If there is an opening bracket then it is always a choice */
        if(open > 0){
            String openingBracket = balancedParentheses + "(";
            permutationBalancedParenthesis(open - 1, close, openingBracket, output);
        } 

        /* There must be an opening bracket previously, which is yet to be closed. */
        if(close > open){
            String closingBracket = balancedParentheses + ")";
            permutationBalancedParenthesis(open, close - 1, closingBracket, output);
        }
    }
}
