"""
. represents occurrence of () in new n
n = 1, () 
n = 2, (.), (). = ()(), (())
n = 3, .()(), (.)(), ().(), ()(.), ()()., .(()), (.()), ((.)), (().), (()).

Avoid duplicates by using a set
"""
class Solution:
    def generateParenthesis(self, n: int) -> List[str]:
        if n == 1:
            return ["()"]
        syms = {"()"}
        for j in range(n-1):
            new_syms = set()
            for sym in syms:
                for i in range(len(sym)):
                    new_syms.add(sym[:i] + "()" + sym[i:])
            syms = new_syms
        return syms

