        ans = [[""]]
        for i in range(1, n+1): 
            tmp = []
            for j in range(i):
                for x in ans[j]:
                    for y in ans[~j]: 
                        tmp.append(f"({x}){y}")
            ans.append(tmp)
        return ans[-1]
