class Solution {
public:
    vector<string> ans;
    void func(string &s , int open , int close){
        if (open == 0 and close == 0){
            ans.push_back(s);
            return;
        }
        if (open > close or open < 0 or close < 0)
            return;
        s.push_back('(');
        func(s , open-1 , close);
        s.pop_back();
        s.push_back(')');
        func(s , open , close-1);
        s.pop_back();
        return;
    }

    vector<string> generateParenthesis(int n) {
        string s;
        func(s , n , n);
        return ans;
    }
};
