vector<string> ans; //container of  all possible answers
void dfs(string& path, int open, int close){
    if(open==0 and close==0){  // here we donot have either of the bracket so add it to our answer
        ans.push_back(path);
        return ;
    }
    if(open>0){ // here we still can add more open brackets in out answers
        path.push_back('(');
        dfs(path, open-1, close); // now update the open 
        path.pop_back(); // yes we did the backtrack here to try out all possibility
    }
    if(close>0 and open < close){ // if and only if we have close brackets and at this current sopt we have more number of open brackets than close ones.
        path.push_back(')');
        dfs(path,open,close-1);
        path.pop_back(); // here also we need to backtrack
    }
}
vector<string> generateParenthesis(int n) {
    string s; // initial path ie. blank
    // in starting we have n number of open and n number of close brackets 
    dfs(s,n,n); // ran a sort of dfs tried out all possibility
    return ans; // return the ans at last when we are done
}

