class Solution {
public:
    vector<string> generateParenthesis(int n) {
        vector<string> result;
        stack<pair<string, pair<int, int>>> st;
        st.push({"", {0, 0}});
        int l, r;
        string s;
        while (!st.empty()){
            pair<string, pair<int, int>> p;
            p = st.top();
            st.pop();
            l = p.second.first;
            r = p.second.second;
            s = p.first;
            if (l < r || l > n || r > n) continue;    // exceed the bundary -> skip the process below
            if (l == n && r == n) result.push_back(s);
            st.push({s+"(", {l+1, r}});
            st.push({s+")", {l, r+1}});
        }
        return result;
    } 
};
