class Solution {
    //这一版不使用递归，速度更快。
    //首先是最厚的括号包裹状态，即一开始左边是连续的左括号，右边是连续的右括号，然后执行以下逻辑：
    //1、右括号不能比左括号多；
    //2、弹出右括号，直到遇到第一个左括号，如果左括号改成右括号仍然合法，则把它改成右括号；否则，左括号继续弹出；
    //3、改完之后一个劲加左括号，直到所有可以用的左括号都加完为止。
    //4、循环一直执行到一开始的连续左括号没有为止。
    //该程序是上一版递归程序的等价版。
public:
    vector<string> generateParenthesis(int n) 
    {
        vector<string> result;
        if(n==0)
            return result;
        string s;
        for(int i=0;i<n;i++)
            s+="(";
        for(int i=0;i<n;i++)
            s+=")";
        int left=n;
        int right=n;
        do
        {
            result.push_back(s);
            while(s!=""&&(s.back()==')'||left-1<right+1))
            {
                if(s.back()==')')
                    right--;
                else
                    left--;
                s.pop_back();
            }
            if(s!="")
            {
                s.back()=')';
                left--;
                right++;
                while(left<n)
                {
                    s+="(";
                    left++;
                }
                while(right<n)
                {
                    s+=")";
                    right++;
                }
            }
        }while(s!="");
        return result;
    }
};

