    private boolean isValid(String pString) {
        int leftCount = 0;
        for (char p : pString.toCharArray()) {
            if (p == '(') {
                leftCount++;
            } else {
                leftCount--;
            } 

            if (leftCount < 0) {
                return false;
            }
        }
        return leftCount == 0;
    }

