public class Solution
{
    public IList<string> GenerateParenthesis(int n) 
    {
        if (n == 0)
        {
            return Array.Empty<string>();
        }

        if (n == 1)
        {
            return new List<string> { "()" };
        }

        List<string> combinations = new List<string>();

        IList<string> previousCombinations = GenerateParenthesis(n - 1);

        foreach (string previous in previousCombinations)
        {
            List<string> current = GetCurrentConfig(previous);
            combinations.AddRange(current);
        }

        return combinations;
    }

    private List<string> GetCurrentConfig(string previous)
    {
        List<char> characters = previous.ToCharArray().ToList();

        List<string> result = new();

        for (int i = 0; i < characters.Count; i++)
        {
            List<char> clone = new List<char>(characters);
            clone.Insert(i, ')');
            clone.Insert(i, '(');
            result.Add(new string(clone.ToArray()));

            if (characters[i] == ')')
            {
                break;
            }
        }

        return result;
    }
}
