    void backtrack(int left, int right, string& track, vector<string>& res) {
        if (right < left) return;
        if (left < 0 || right < 0) return;
        if (left == 0 && right == 0) {
            res.push_back(track);
            return;
        }

        track.push_back('('); 
        backtrack(left - 1, right, track, res);
        track.pop_back(); 

        track.push_back(')'); 
        backtrack(left, right - 1, track, res);
        track.pop_back(); 
    }
};
