    const results = []; // khởi tạo mảng kết quả chứa các chuỗi ngoặc đúng định dạng

    function generate(str, left, right) { // khai báo hàm đệ quy generate với 3 tham số
        if (left === 0 && right === 0) { // nếu số lượng dấu ngoặc mở và đóng bằng 0
            results.push(str); // thêm chuỗi str vào mảng kết quả
            return; // thoát khỏi hàm
        }
        if (left > 0) { // nếu còn dấu ngoặc mở chưa sử dụng hết
            generate(str + "(", left - 1, right); // thêm dấu ngoặc mở vào chuỗi str và giảm số lượng dấu ngoặc mở còn lại đi 1
        }
        if (right > left) { // nếu số lượng dấu ngoặc đóng còn lại nhiều hơn dấu ngoặc mở
            generate(str + ")", left, right - 1); // thêm dấu ngoặc đóng vào chuỗi str và giảm số lượng dấu ngoặc đóng còn lại đi 1
        }
    }

    generate("", n, n); // bắt đầu chạy hàm generate với chuỗi rỗng và số lượng dấu ngoặc mở và đóng bằng n
    return results; // trả về mảng kết quả
}
