class Solution {
    public List<String> generateParenthesis(int n) {
       List<String> ans=new ArrayList<>();

       helper(n*2,"",0,0,ans,0);

       return ans;
    }

    public void helper(int n,String asf,int oc,int nc,List<String> ans,int idx)
    {
        if(idx==n)
        {
            if(isValid(asf))
            {
                ans.add(asf);
            }
            return;
        }

        helper(n,asf+"(",oc+1,nc,ans,idx+1);
         helper(n,asf+")",oc,nc+1,ans,idx+1);
    }
     public static boolean isValid(String s) {
    // Inilization of Stack
        Stack<Character> st = new Stack<>();
    // Iterating through the given String
        for (int i = 0; i < s.length(); i++) {
    // Push Open Brackets in the Stack
            if (s.charAt(i) == '{' || s.charAt(i) == '(' || s.charAt(i) == '[') {
                st.push(s.charAt(i));
            }
    // checking for the empty stack because if empty this means there is no open bracket at the start which is an invaild string.
            else if(st.empty()){
                return false;
            }
    // Check for the close brackets pair on the top and pop them
            else if (st.peek() == '{' && s.charAt(i) == '}' ) {
                st.pop();
            } else if (st.peek() == '(' && s.charAt(i) == ')' ) {
                st.pop();
            } else if (st.peek() == '[' && s.charAt(i) == ']' ) {
                st.pop();
            }else{
                return false;
            }

        }
        return st.empty();


    }


}

