    def maxProfit(self, prices: List[int]) -> int:
        def recursive(i):
            if i <= 0:
                return 0
            if i in mp:
                return mp[i]
            max_profit = recursive(i - 1)
            for j in range(i):
                tmp = prices[i] - prices[j] + recursive(j - 1)
                max_profit = max(max_profit, tmp)
            mp[i] = max_profit
            return mp[i]

        mp = {}
        return recursive(len(prices) - 1)
