        int curHold = max( prevHold, prevNotHold - prices[ day ] );

        // either keep not-hold, or sell out stock today at stock price
        int curNotHold = max( prevNotHold, prevHold + prices[ day ] );        

        return tuple<int, int>{ curHold, curNotHold};
    }
};
