	class Solution {
	public:
		int f(int i,int buy,vector<int>& p,int n){
			if(i==n) return 0;
			if(buy){
				int buy=-p[i]+f(i+1,0,p,n);
				int notbuy=f(i+1,1,p,n);
				return max(buy,notbuy);
			}
			else{
				int sell= p[i]+f(i+1,1,p,n);
				int notsell=f(i+1,0,p,n);
				return max(sell,notsell);
			}
		}

		int maxProfit(vector<int>& p){
			int n=p.size();
			return f(0,1,p,n);
		}
	};

# Method -2 [Memoization]	

![image](https://assets.leetcode.com/users/images/06275f0c-6fe8-4f3f-b1b0-3b59e3827b04_1661601266.508214.png)

**T->O(2 * n) && S->O(2 * n) + O(n) [Recursion Stack Space]**

	class Solution {
	public:
		int f(int i,int buy,vector<int>& p,int n,vector<vector<int>>& dp){
			if(i==n) return 0;
			if(dp[i][buy]!=-1) return dp[i][buy];
			int pro1=INT_MIN,pro2=INT_MIN;
			if(buy){
				int buy=-p[i]+f(i+1,0,p,n,dp);
				int notbuy=f(i+1,1,p,n,dp);
				pro1=max(buy,notbuy);
			}
			else{
				int sell= p[i]+f(i+1,1,p,n,dp);
				int notsell=f(i+1,0,p,n,dp);
				pro2=max(sell,notsell);
			}
			return dp[i][buy]=max(pro1,pro2);
		}

		int maxProfit(vector<int>& p){
			int n=p.size();
			vector<vector<int>> dp(n,vector<int>(2,-1));
			return f(0,1,p,n,dp);
		}
	};

# Method - 3 [Tabulation]
![image](https://assets.leetcode.com/users/images/feae764c-e600-4e17-9bd9-009d414230bf_1661603061.6112258.png)

**T->O(2 * n) && S->O(2 * n)**

	class Solution {
	public:
		int maxProfit(vector<int>& p){
			int n=p.size();
			vector<vector<int>> dp(n+1,vector<int>(2,0));
			for(int i=n-1;i>=0;i--){
				for(int buy=0;buy<=1;buy++){
					int profit;
					if(buy){
						int buy=-p[i]+dp[i+1][0];
						int notbuy=dp[i+1][1];
						profit=max(buy,notbuy);
					}
					else{
						int sell= p[i]+dp[i+1][1];
						int notsell=dp[i+1][0];
						profit=max(sell,notsell);
					}
					dp[i][buy]=profit;
				}
			}
			return dp[0][1];
		}
	};

# Method  - 4 [SpaceOptimization]

![image](https://assets.leetcode.com/users/images/b0b417fe-4d84-4007-a2e6-e413b1277446_1661603341.085726.png)

**T->O(2 X n) && S->O(2 X 2)**

	class Solution {
	public:
		int maxProfit(vector<int>& p){
			int n=p.size();
			vector<int> prev(2,0),curr(2,0);
			for(int i=n-1;i>=0;i--){
				for(int buy=0;buy<=1;buy++){
					int profit;
					if(buy){
						int buy=-p[i]+prev[0];
						int notbuy=prev[1];
						profit=max(buy,notbuy);
					}
					else{
						int sell= p[i]+prev[1];
						int notsell=prev[0];
						profit=max(sell,notsell);
					}
					curr[buy]=profit;
				}
				prev=curr;
			}
			return prev[1];
		}
	};

