class Solution {
public:
    int maxProfit(vector<int>& prices) {
        int n = prices.size();
        vector <int> ahead(2, 0), cur(2, 0);
        ahead[0] = ahead[1] = 0;
        for (int idx = n-1; idx >= 0; idx--) {
            for (int buy = 0; buy <= 1; buy++) {
                int profit;
                if (buy == 1) 
                    profit = max (-prices[idx] + ahead[0], 0 + ahead[1]);
                else
                    profit = max (prices[idx] + ahead[1], 0 + ahead[0]);
                cur[buy] = profit; 
            }
            ahead = cur;
        }
        return ahead[1];
    }
};
```

