        if (prices == null || prices.length == 0) return 0;

        memo = new int[prices.length];
        return maxProfitFrom(prices, 0);
    }

    private int maxProfitFrom(int[] prices, int start) {
        if (start == prices.length) return 0;
        if (memo[start] != 0) return memo[start];

        int maxProfit = 0;
        for (int lo = start; lo < prices.length - 1; lo++) {
            int maxProfitBuyOnLo = 0;
            for (int hi = lo + 1; hi < prices.length; hi++) {
                if (prices[hi] > prices[lo]) maxProfitBuyOnLo = Math.max(maxProfitBuyOnLo, prices[hi] - prices[lo] + maxProfitFrom(prices, hi + 1));
            }
            maxProfit = Math.max(maxProfit, maxProfitBuyOnLo);
        }

        memo[start] = maxProfit;
        return maxProfit;
    }
