class Solution {
public:
      // we have created an dp array of size of array length 
	  // here 2nd dimension of dp array we are using for toggling the buy and sell values
	  // 0 -  no stock on hold
	  // 1 - mean we have one stock we need to first sell it as given in question
    int dp[30000][2];
    int solve(vector<int>& v,int i,int flag=0){
        if(i>=v.size()) return 0;  // if index is out of bound no profit will be generated so return zero
        if(dp[i][flag]!=-1)
           return dp[i][flag]; // if this problem occured previously then we will directly return from here
        int profit=0; 
        if(flag==0){   // if flag==0  which means we don't have any stock
		  // first we need to buy one
		  // it implies two possibilities  either we buy or we dont't buy 
            int x=solve(v,i+1,1)-v[i];  // if we buy then this stock price is deducted from our overall profit
            int y=solve(v,i+1,0); // or we can also skip this day
			// now after recursively solving both the case we get the profit amount from both scenario
            profit=max(x,y); // we will choose maximum
// here thing to be noticed that if we buy the stock then flag value become 1 (as we considered above)
			// or if we don't buy it remain as it is
        }
        else{
		    // flag==1 
			// we again have two possibilities either we can sell the stock or we can skip this day
            int x=solve(v,i+1,0)+v[i];  // if we sell the stock we will get some profit which we will add in  overall profit ( flag will beome 0 )
            int y=solve(v,i+1,1); // we can also skip the day flag remain as it is
			//after taking both the cases and solving problem recursively we will take maximum profit
            profit=max(x,y);
        }
        return dp[i][flag]=profit; // for this particular day at this particular flag value we are storing our //answer for repeating sub problems
    }
    int maxProfit(vector<int>& prices) {
        memset(dp,-1,sizeof(dp));
        return solve(prices,0,0); // here we are passing flag value equal to zero which mean we don't 
		// have any stock initially
    }
};

