class Solution {
    public int maxProfit(int[] prices) {
        int currentstock = prices[0];   
        int totalprofit = 0;
        for(int i=0; i<prices.length-1; ++i) {
            if(prices[i] > prices[i+1]) {   //Reseting current stock
                currentstock = prices[i+1];
            } else {
                //Checking for max value in right of current stock
                while(i<(prices.length-1) && prices[i] < prices[i+1]) { 
                    ++i;
                }
                //Calculating profit
                totalprofit += prices[i] - currentstock;
                //Setting current stock to next index of maxi value
                if(i<prices.length-1) {
                    currentstock = prices[i+1];
                }
            }
        }

        return totalprofit;
    }
}

