    public int helper(int []prices,boolean buy,boolean sell,int buyPr,int index){
        if(index==prices.length){
            return 0;
        }
        if(dp[index][buyPr]!=-1){
            return dp[index][buyPr];
        }
        int ans=0;
        if(buy){
            //two option either to sell orr not to sell
            int ansCheck1=helper(prices,false,sell,0,index+1)+(prices[index]);
            int ansCheck2=helper(prices,buy,sell,buyPr,index+1);
            ans=Math.max(ansCheck1,ansCheck2);
        }else{
            //two option either to buy the stock or to sell the stock
            int ansCheck1=helper(prices,true,sell,1,index+1)-prices[index];
            int ansCheck2=helper(prices,buy,sell,buyPr,index+1);
            ans=Math.max(ansCheck1,ansCheck2);
        }
        return dp[index][buyPr]=ans;
    }
    public int maxProfit(int[] prices) {
        dp=new int[prices.length+1][3];
        for(int []arr:dp){
            Arrays.fill(arr,-1);
        }
        return helper(prices,false,false,0,0);
    }

