function maxProfit(prices: number[]): number {
    let lastBuy = -1;
    let lastSell = -1;
    let profit = 0;
    const havingStock = () => lastBuy > lastSell;
    const buy = (atIndex: number) => {
        lastBuy = atIndex;
    }
    const sell = (atIndex: number) => {
        lastSell = atIndex;
        const thisProfit = prices[lastSell] - prices[lastBuy];
        profit += thisProfit;
    }
    for (let i = 0; i < prices.length; i++) {
        const isLastItem = i === prices.length - 1;
        if (isLastItem && havingStock()) {
            sell(i);
            break;
        }

        const willUp = prices[i + 1] > prices[i];
        if (willUp && !havingStock()) {
            buy(i);
        } 

        const willDown = prices[i + 1] < prices[i]
        if (willDown && havingStock()) {
            sell(i);
        }
    }
    return profit;
};
