    const rdepth = depthRightMostNode(r);

    if (ldepth === rdepth)					// same height for  left & right sub tree
      return Math.pow(2, ldepth) - 1;		//   so, 0th level has 2**0 - 1 = 1, 1st level has 2**1 - 1 = 3

    return 1 + dfs(r.left) + dfs(r.right) // may be, left subtree has same height
  }
  return dfs(r);
};

