class Solution {
    public int countNodes(TreeNode root) {
        int h = height(root);
        return h == 0 ? 0 : binarySearch(root, (int)Math.pow(2, h - 1), (int)Math.pow(2, h)) - 1;
    }

    private int height(TreeNode root) {
        return root == null ? 0 : 1 + height(root.left);
    }

    private int binarySearch(TreeNode root, int st, int en) {
        if(st == en) {
            return st;
        }
        int mid = st + (en - st) / 2;
        Stack<Integer> step = new Stack<>();
        int curr = mid;
        while(curr > 1) {
            step.push(curr % 2);
            curr /= 2;
        }
        TreeNode node = root;
        while(!step.isEmpty()) {
            node = step.pop() == 0 ? node.left : node.right;
        }
        return node == null ? binarySearch(root, st, mid) : binarySearch(root, mid + 1, en);
    }
}
```

