        if(root==NULL) return;

        ans++;
        preorder(root->left,ans);
        preorder(root->right,ans); 
    }
    int countNodes(TreeNode* root) {
        int ans=0;
        preorder(root,ans); // in / post 
        return ans;
    }
};
