class Solution {
    int getHeight(TreeNode *node) {
        int height = 0;
        while (node && node->left) {
            ++height;
            node = node->left;
        }
        return height;
    }

    bool exist(TreeNode* node, int idx, int h) {
        int mask = (1 << h) >> 1; // Use (1 << h) >> 1 to replace 1 << (h-1). Consider h = 0.
        while (mask) {
            node = (mask & idx) ? node->right : node->left;
            mask >>= 1;

            if (!node) {
                return false;
            }
        }
        return true;
    }

public:
    int countNodes(TreeNode* root) {
        if (!root) return 0;
        int h = getHeight(root);
        int start = 1 << h, end = (start << 1);
        while (start < end) {
            int mid = (start + end) >> 1;
            if (exist(root, mid, h)) start = mid+1;
            else end = mid;
        }
        return (1 << h)-1 + (start - (1<<h));
    }
};
