class Solution:
    def countNodes(self, root: TreeNode) -> int:
        if not root:
            return 0

        h = self.get_height(root)
        lo, hi = 0, (2**h)-1
        furthest_right = 0
        while lo <= hi:
            m = lo + (hi - lo) // 2
            if self.has_leaf(root, m, h-1):
                furthest_right = m
                lo = m + 1
            else:
                hi = m - 1
        return (2**h - 1) + (furthest_right + 1) 


    def has_leaf(self, root: TreeNode, index: int, bit: int) -> TreeNode:
        """
        Checks to see if the tree has a leaf node at this index 
        """
        while root and bit >= 0:
            if index & (1 << bit):
                root = root.right
            else:
                root = root.left
            bit -= 1

        return root


    def get_height(self, root: TreeNode) -> int:
        """
        Gets the height of binary tree, which is the number of edges on the path from root
        to the furthest leaf node which is guaranteed to be the leftmost leaf based on definition
        of complete binary tree
        """
        count = -1
        while root:
            count += 1
            root = root.left
        return count
